console.log('Hola mundo');

// Variables y constantes 

// var -> ya no se usa 

const nombre = 'paul';
const apellido = 'salazar';
let valor = 4;
valor = 9;

if (true) {
    let valor = 0;
    console.log(valor);

}


console.log(valor);
