import getHeroeById from './bases/08-import';


const promesa = new Promise( (resolve, reject) =>{
    setTimeout( () => {
        const heroe = getHeroeById(2);
        // console.log(heroe);
        // console.log('2 segundos despues')
         resolve(heroe);
    }, 2000)

});


promesa.then( (resp) => {
    console.log('Heroe')
})
