//desestructuracion 
// asignación desestructurante 

const persona = {
    nombre: 'paul',
    edad: 45,
    clave: 'asdas'
};


console.log(persona.nombre);
console.log(persona.edad);
console.log(persona.clave);

console.log('-------------------------');

const { nombre, edad, clave } = persona;

console.log(nombre);
console.log(edad);
console.log(clave);



const retornaPersona = ({ nombre, edad, rango = 'lol' }) => {
    console.log(nombre, edad, rango);

}

retornaPersona(persona);


const useContext = ({clave, nombre, edad, rango = 'lol' }) => {
    // console.log(nombre, edad, rango);
    return{
        nombreClave: clave,
        anios: edad
    }
}

const data = useContext(persona);

const {nombreClave, anios} = useContext(persona);


console.log(data);
console.log('*-----------------*');
console.log(nombreClave);
console.log(anios);



const useContext1 = ({clave, nombre, edad, rango = 'lol' }) => {
    // console.log(nombre, edad, rango);
    return{
        nombreClave2: clave,
        anios1: edad,
        latlng:{
            lat: -123123123,
            lng
        }
    }
}

const {nombreClave2, anios1} = useContext1(persona);

console.log('*-----------------*');
console.log(nombreClave);
console.log(anios);
